import { TextField } from "@material-ui/core"
import { Form } from "react-bootstrap"
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { useEffect, useState } from "react";
import AdapterDateFns from '@mui/lab/AdapterDateFns';
import LocalizationProvider from '@mui/lab/LocalizationProvider';
import DatePicker from '@mui/lab/DatePicker';
import { Grid } from "@mui/material";
import Picker from 'emoji-picker-react';


function CreateItem(){
  const [timeframe, setTimeframe] = useState("")
  const [name, setName] = useState(null)
  // const [date, setDate] = useState(null)
  const [extension, setExtension] = useState(null)
  const [open, setOpen] = useState(false)
  const URL = "https://joepera.com/product/pine-hat/"
  const [stock, setStock] = useState("")

  useEffect(()=>{
    fetch(URL)
    .then(r => r.text())
    .then(s => {
      let parser = new DOMParser();
      let doc = parser.parseFromString(s, 'text/html')

      // Get the image file
      let stock = doc.querySelector('.stock')
      stock.className === "stock in-stock" ? setStock("In Stock") : setStock("Out of Stock")
    })
  },[])


  // return (
  //   <button>{stock}</button>
  // )


  function handleClose(){
    setOpen(false)
  }

  function handleOpen(){
    setOpen(true)
  }

  return(
    <div className="create-item">
      <a >{stock}</a>
      <Form  >
          <FormControl sx={{m: 2, width: 300 }}>
            <TextField 
              required
              variant="outlined"
              label= "Name"
              name= "name"
              onChange={e=>setName(e.target.value)}
            />
          </FormControl>
        {/* <Grid container spacing={2} > */}
          {/* <Grid item > */}
            {/* <FormControl sx={{m:2, width: 300 }}>
              <LocalizationProvider dateAdapter={AdapterDateFns} >
                <DatePicker 
                  label="Select Date"
                  value={date}
                  onChange={(newDate) => {setDate(newDate)}}
                  renderInput={(params) => <TextField  variant="outlined" {...params} />}
                />       
              </LocalizationProvider>
            </FormControl> */}
          {/* </Grid> */}
          {/* <Grid> */}
            <FormControl sx={{m: 2, width: 300 }}>
              <TextField 
                required
                variant="outlined"
                label= "Extension"
                name= "extension"
                type="number"
                onChange={e=>setExtension(e.target.value)}
              />
            </FormControl>
          {/* </Grid> */}
          {/* <Grid item> */}
            <FormControl sx={{m:2, width: 300 }}>
              <InputLabel>Timeframe</InputLabel>
              <Select
                label="Timeframe"
                open={open}
                onClose={handleClose}
                onOpen={handleOpen}
                value={timeframe}
                variant="outlined"
                onChange={e=>  setTimeframe(e.target.value)}
              >
                <MenuItem value="">
                  <em>Select One</em>
                </MenuItem>
                <MenuItem value="Day">Day</MenuItem>
                <MenuItem value="Week">Week</MenuItem>
                <MenuItem value="Month">Month</MenuItem>
                <MenuItem value="Year">Year</MenuItem>
              </Select>
            </FormControl>

          {/* </Grid> */}

        {/* </Grid> */}
      </Form>

      <h1> Results: </h1>
      {/* <h1>{new Date(date).toLocaleDateString()}</h1> */}
      {timeframe ? <h1>Set {name.toLowerCase()} to be consumed within {extension.toLocaleString()} {extension > 1 ? timeframe.toLowerCase() + "s" : timeframe.toLowerCase()} past its expiration date?</h1> : <></> }

      </div>  
  )
}
export default CreateItem
