import { TextField } from "@material-ui/core"
import { Form } from "react-bootstrap"
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Button from '@mui/material/Button';
import { useState } from "react";
import AdapterDateFns from '@mui/lab/AdapterDateFns';
import LocalizationProvider from '@mui/lab/LocalizationProvider';
import DatePicker from '@mui/lab/DatePicker';
import DesktopDatePicker from "@mui/lab/DesktopDatePicker";
import { createTheme } from "@material-ui/core";
import { ThemeProvider } from "@material-ui/styles";
import { red } from "@mui/material/colors";

function CreateItem(){
    const [category, setCategory] = useState("");
    const [name, setName] = useState("");
    const [open, setOpen] = useState(false);
    const defaultMaterialTheme = createMuiTheme({
        spacing: 2,
      });

    const FormTemplate = (label, name) => {
        return(
        <TextField 
        required
        variant="outlined"
        // id="outline-required"
        label= {label}
        name= {name}
        onChange={e=>handleFunc(name, e)}
      ></TextField>
        )
    }

    function handleFunc(name, e){
        e.preventDefault()
        if(name === "name"){
            setName(e.target.value + "name")
        }
        else if(name === "exp_date"){
            setName(e.target.value + "exp_date")
        } else {
            console.log("oops")
        }
    }

    // const handleChange = (event) => {
    //   setCategory(event.target.value);
    // };

    const handleClose = () => {
      setOpen(false);
    };

    const handleOpen = () => {
      setOpen(true);
    };

    const SelectTemplate = (selection,o1,o2,o3,o4) => {     
              return (
                <div>
                  <Button sx={{ display: 'block', mt: 2 }} onClick={handleOpen}>
                    Select a {selection}
                  </Button>
                  <FormControl sx={{ m: 1, minWidth: 120 }}>
                    <InputLabel>{selection}</InputLabel>
                    <Select
                      open={open}
                      onClose={handleClose}
                      onOpen={handleOpen}
                      value={category}
                      label={selection}
                      onChange={e=>  setCategory(e.target.value)}
                    >
                      <MenuItem value="">
                        <em>Select One</em>
                      </MenuItem>
                      <MenuItem value={o1}>{o1}</MenuItem>
                      <MenuItem value={o2}>{o2}</MenuItem>
                      <MenuItem value={o3}>{o3}</MenuItem>
                      <MenuItem value={o4}>{o4}</MenuItem>

                    </Select>
                  </FormControl>
                </div>
              );
    }

    console.log(name)
    console.log(category)

    const [value, setValue] = useState(null);



    return(
        <div>
            <h1>CreateItem</h1>


        <Form>
            {FormTemplate("Name", "name")}
            {FormTemplate("Expiration Date", "exp_date")}
            {/* {FormTemplate("Storage Type", "storage")} */}
            {SelectTemplate("Timeframe","day", "month","week","year")}


        </Form>
        <ThemeProvider theme={defaultMaterialTheme}>
        <LocalizationProvider dateAdapter={AdapterDateFns} >
        <DatePicker 

          label="Select Date"
          value={value}
          minDate={new Date('2017-01-01')}
          onChange={(newValue) => {
            setValue(newValue);
          }}
          renderInput={(params) => <TextField {...params} />}
        />       
    </LocalizationProvider>
    </ThemeProvider>

        </div>
    )
}
export default CreateItem
