import {TextField } from "@material-ui/core";
// import { Form } from "react-bootstrap";
// import { Button, FormControl } from "@mui/material";
// import Button from "@material-ui/core/Button";
// import { TextField } from "material-ui";

function SignUpForm({setUsername, setEmail, setPassword, setPasswordConfirmation}) {



  const userField = (name) => {
    let label = name.split("_").map(word => word[0].toUpperCase()+word.slice(1, word.length)).toString().replace(","," ") 
    return(
      <TextField 
      required
      variant="outlined"
      label={label}
      name={name}
      onChange={
        e=>{ const {name, value} = e.target
        setUsername({[name]: value})}}
  ></TextField>
    )
  }

  return (
    <>
      {/* <TextField 
        required
        variant="outlined"
        // id="outline-required"
        label="UserName"
        name='username'
        onChange={
          e=>{ const {name, value} = e.target
          setUsername({[name]: value})}}
      ></TextField> */}
      {userField("username")}
      {userField("email")}
      {userField("password")}
      {userField("password_confirmation")}

      <TextField 
        required
        variant="outlined"
        // id="outline-required"
        label="Email"
        name='email'
        onChange={e=>setEmail({name:e.target.name, value: e.target.value})}
      ></TextField>

      <TextField 
        required
        variant="outlined"
        // id="outline-required"
        label="Password"
        name="password"
        type="password"
        onChange={e=>setPassword({name:e.target.name, value: e.target.value})}
      ></TextField>

      <TextField 
        required
        variant="outlined"
        // id="outline-required"
        label="Password Confirmation"
        name="password_confirmation"
        type="password"
        onChange={e=>setPasswordConfirmation({name:e.target.name, value: e.target.value})}
      ></TextField>
    </>
  );
}
export default SignUpForm;
