import { TextField } from "@material-ui/core"
import { Form } from "react-bootstrap"
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Button from '@mui/material/Button';
import { useState } from "react";
import AdapterDateFns from '@mui/lab/AdapterDateFns';
import LocalizationProvider from '@mui/lab/LocalizationProvider';
import DatePicker from '@mui/lab/DatePicker';
import DesktopDatePicker from "@mui/lab/DesktopDatePicker";
import { createTheme } from "@material-ui/core";
import { ThemeProvider } from "@material-ui/styles";
import { red } from "@mui/material/colors";
import { Grid } from "@mui/material";

function CreateItem(){
  const [category, setCategory] = useState("");
  const [name, setName] = useState("");
  const [open, setOpen] = useState(false);
  const defaultMaterialTheme = createTheme({});

  const FormTemplate = (label, name) => {
      return(
      <TextField 
      required
      variant="outlined"
      // id="outline-required"
      label= {label}
      name= {name}
      onChange={e=>handleFunc(name, e)}
    ></TextField>
      )
  }

  function handleFunc(name, e){
      e.preventDefault()
      if(name === "name"){
          setName("name= " + e.target.value)
      }
      else {
          setName("oops")
      }
  }

  // const handleChange = (event) => {
  //   setCategory(event.target.value);
  // };

  const handleClose = () => {
    setOpen(false);
  };

  const handleOpen = () => {
    setOpen(true);
  };

  const SelectTemplate = (selection,o1,o2,o3,o4) => {     
            return (
                <FormControl sx={{ maxWidth: 200 }}>
                  <InputLabel>{selection}</InputLabel>
                  <Select
                    open={open}
                    onClose={handleClose}
                    onOpen={handleOpen}
                    value={category}
                    label={selection}
                    onChange={e=>  setCategory(e.target.value)}
                  >
                    <MenuItem value="">
                      <em>Select One</em>
                    </MenuItem>
                    <MenuItem value={o1}>{o1}</MenuItem>
                    <MenuItem value={o2}>{o2}</MenuItem>
                    <MenuItem value={o3}>{o3}</MenuItem>
                    <MenuItem value={o4}>{o4}</MenuItem>

                  </Select>
                </FormControl>
            );
  }

  const [date, setDate] = useState([]);



  return(
      <div className="create-item">
      <Form  >
      <div>

      <FormControl sx={{ m: 1, width: 618 }}>

          {FormTemplate("Name", "name")}
      </FormControl>
      </div>

<Grid container spacing={2} >
          <Grid item >
          <FormControl sx={{width: 300 }}>

          <LocalizationProvider dateAdapter={AdapterDateFns} >
              <DatePicker 
              label="Select Date"
              value={date}
              onChange={(newDate) => {
                  setDate(newDate);
              }}
              renderInput={(params) => <TextField  variant="outlined" {...params} />}
              />       
          </LocalizationProvider>
          </FormControl>

          </Grid>
          <Grid item>
            <FormControl sx={{width: 300 }}>
              <InputLabel>Timeframe</InputLabel>
                <Select
                  open={open}
                  onClose={handleClose}
                  onOpen={handleOpen}
                  value={category}
                  onChange={e=>  setCategory(e.target.value)}>
                  <MenuItem value="">
                    <em>Select One</em>
                  </MenuItem>
                  <MenuItem value="Day">Day</MenuItem>
                  <MenuItem value="Week">Week</MenuItem>
                  <MenuItem value="Month">Month</MenuItem>
                  <MenuItem value="Year">Year</MenuItem>
                </Select>
              </FormControl>
            </Grid>
          </Grid>
        </Form>

      <h1>Results:</h1>
              <div>
                <div>
                  <h1>{new Date({date}).toLocaleDateString()}</h1>
                </div>
                  <h1>{category}</h1>
                  <h1>{name}</h1>

              </div>


      </div>

  )
}
export default CreateItem
