import { Dialog, DialogContent, DialogTitle, Button } from "@material-ui/core";
import React, { useState } from "react";
import SignUpForm from "./SignUpForm";
import { toast, Flip } from "react-toastify";
import { Form } from "react-bootstrap";

function Signup({ setUser }) {
  const [email, setEmail] = useState([]);
  const [username, setUsername] = useState([]);
  const [password, setPassword] = useState([])
  const [passwordConfirmation, setPasswordConfirmation] = useState([]);
  const [open, setOpen] = React.useState(false);
  // const body = {username, email, password, passwordConfirmation}

  const handleClickToOpen = () => {
    setOpen(true);
  };

  const handleToClose = () => {
    setOpen(false);
  };

  function handleSignUp(e) {
    e.preventDefault();
    const data = new FormData()
    data.append(username)
    data.append(email)
    data.append(password)
    data.append(passwordConfirmation)
    // fetch("/signup", {
    //   method: "POST",
    //   headers: {
    //     "Content-Type": "application/json",
    //     Accept: "application/json",
    //   },
    //   body: data,
    // }).then((r) => {
    //   if (r.ok) {
    //     r.json().then((user) => setUser(user));
    //     handleToClose()
    //   } else {
    //     toast.error( r.errors, {
    //       autoClose: 1000,
    //       hideProgressBar: true,
    //       transition: Flip,
    //       position: "top-center"
    //     })
    //   }
    // })
    console.log(data)
  }


  return (
    <>
      <Button variant="outlined" color="primary" onClick={handleClickToOpen}>
        Signup
      </Button>

      <Dialog open={open} onClose={handleToClose} onSubmit={handleSignUp}>
        <DialogTitle>{"Sign Up"}</DialogTitle>
        <DialogContent>
          <Form onSubmit={handleSignUp}>
            <SignUpForm 
            setUsername={setUsername}
            setEmail={setEmail}
            setPassword={setPassword}
            setPasswordConfirmation={setPasswordConfirmation}
            handleSignUp={handleSignUp}
            handleToClose={handleToClose}
            />
            <Button type="submit" color="primary" autoFocus>
              Submit
            </Button>
            <Button onClick={handleToClose} color="secondary" autoFocus>
              Close
            </Button>
          </Form>
        </DialogContent>
      </Dialog>
    </>
  );
}
export default Signup;
