import { TextField, Button } from "@material-ui/core"
import { Form } from "react-bootstrap"
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { useState } from "react";
import EmojiPicker from "emoji-picker-react";
import Accordion from '@mui/material/Accordion';
import {AccordionDetails} from '@mui/material/AccordionDetails';
import AccordionSummary from '@mui/material/AccordionSummary';
import Typography from '@mui/material/Typography';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';

function CreateItem(){
  const [timeframe, setTimeframe] = useState("")
  const [name, setName] = useState(null)
  const [extension, setExtension] = useState(null)
  const [open, setOpen] = useState(false)
  const [emoji, setEmoji] = useState("")
  const [expanded, setExpanded] = useState(false);

  function handleClose(){
    setOpen(false)
  }

  function handleOpen(){
    setOpen(true)
  }

  function handleEmoji(e){
    setEmoji(e.target.src)
    setExpanded(false)
  }



  const handleChange = (panel) => (event, isExpanded) => {
    setExpanded(isExpanded ? panel : false);
  };

  return(
    <div className="create-item">
      <Form  >

        <FormControl sx={{m: 2 }}>
          <TextField 
            required
            variant="outlined"
            label= "Name"
            name= "name"
            onChange={e=>setName(e.target.value)}
          />
        </FormControl>

        <FormControl sx={{m: 2 }}>
          <TextField 
            required
            variant="outlined"
            label= "Extension"
            name= "extension"
            type="number"
            onChange={e=>setExtension(e.target.value)}
          />
        </FormControl>
        <FormControl sx={{m:2}}>
          <InputLabel>Timeframe</InputLabel>
          <Select
            label="Timeframe"
            open={open}
            onClose={handleClose}
            onOpen={handleOpen}
            value={timeframe}
            variant="outlined"
            onChange={e=>  setTimeframe(e.target.value)}
          >
            <MenuItem value="">
              <em>Select One</em>
            </MenuItem>
            <MenuItem value="Day">Day</MenuItem>
            <MenuItem value="Week">Week</MenuItem>
            <MenuItem value="Month">Month</MenuItem>
            <MenuItem value="Year">Year</MenuItem>
          </Select>
        </FormControl>
        <FormControl sx={{m:2}}>
        <Accordion expanded={expanded === 'panel1'} onChange={handleChange('panel1')} >
        <AccordionSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="panel1bh-content"
          id="panel1bh-header"
        >
          <Typography sx={{ margin: "auto"}}>
            Choose an Icon (optional)
          </Typography>
        </AccordionSummary>
        <AccordionDetails>
          <EmojiPicker
            disableSkinTonePicker={true}
            onEmojiClick={handleEmoji}
          />
        </AccordionDetails>
      </Accordion>
        </FormControl>

      </Form>

      {/* make this a function */}
      <h1> Results: </h1>
      {timeframe ? <div><h1>Set {name.toLowerCase()} to be consumed within <em>{extension.toLocaleString()} {extension > 1 ? timeframe.toLowerCase() + "s" : timeframe.toLowerCase()}</em> past its expiration date?</h1> <Button>Confirm</Button> </div>: <></> }
      <img src={emoji} alt="emoji"/>
      </div>  
  )
}
export default CreateItem
