import {TextField } from "@material-ui/core";
// import { Form } from "react-bootstrap";
// import { Button, FormControl } from "@mui/material";
// import Button from "@material-ui/core/Button";
// import { TextField } from "material-ui";

function SignUpForm({setUsername, setEmail, setPassword, setPasswordConfirmation}) {



  const userField = (name) => {
    let label = name.split("_").map(word => word[0].toUpperCase()+word.slice(1, word.length)).toString().replace(","," ") 
    return(
      <TextField 
      required
      variant="outlined"
      label={label}
      name={name}
      onChange={
        e=>{ const {name, value} = e.target
        setUsername({[name]: value})}}
  ></TextField>
    )
  }

  return (
    <>
      {userField("username")}
      {userField("email")}
      {userField("password")}
      {userField("password_confirmation")}
    </>
  );
}
export default SignUpForm;
