import { TextField } from "@material-ui/core"
import { Form } from "react-bootstrap"
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Button from '@mui/material/Button';
import { useState } from "react";

function CreateItem(){


    const FormTemplate = (label, name) => {
        return(
        <TextField 
        required
        variant="outlined"
        id="outline-required"
        label= {label}
        name= {name}
        onChange={e=>console.log(e.target.value)}
      ></TextField>
        )
    }

    const [something, setSomething] = useState('');
    const [open, setOpen] = useState(false);

    const handleChange = (event) => {
      setSomething(event.target.value);
    };

    const handleClose = () => {
      setOpen(false);
    };

    const handleOpen = () => {
      setOpen(true);
    };

    const SelectTemplate = (selection,value,o1,o2,o3,o4) => {     
              return (
                <div>
                  <Button sx={{ display: 'block', mt: 2 }} onClick={handleOpen}>
                    Select a {selection}
                  </Button>
                  <FormControl sx={{ m: 1, minWidth: 120 }}>
                    <InputLabel>{selection}</InputLabel>
                    <Select
                      open={open}
                      onClose={handleClose}
                      onOpen={handleOpen}
                      value={value}
                      label={selection}
                      onChange={handleChange}
                    >
                      <MenuItem value="">
                        <em>None</em>
                      </MenuItem>
                      <MenuItem value={o1}>{o1}</MenuItem>
                      <MenuItem value={o2}>{o2}</MenuItem>
                      <MenuItem value={o3}>{o3}</MenuItem>
                      <MenuItem value={o4}>{o4}</MenuItem>

                    </Select>
                  </FormControl>
                </div>
              );
    }

    console.log(something)




    return(
        <div>
            <h1>CreateItem</h1>


        <Form>
            {FormTemplate("Name", "name")}
            {FormTemplate("Expiration Date", "exp_date")}
            {FormTemplate("Storage Type", "storage")}
            {SelectTemplate("Select Something","Something","1", "2","3","4")}


        </Form>



        </div>
    )
}
export default CreateItem
