import {TextField } from "@material-ui/core";

function SignUpForm({setUsername, setEmail, setPassword, setPasswordConfirmation}) {

  function handleChange(name, value){
    // const {name, value} = e.target
    const ret = {[name]: value}
    if(name === "username") {setUsername(ret)}
    else if(name === "email") {setEmail(ret)}
    else if(name === "password") {setPassword(ret)}
    else if(name === "password_confirmation") {setPasswordConfirmation(ret)}
    }

  const userField = (label, type="text") => {
    // let label = name.split("_").map(word => word[0].toUpperCase()+word.slice(1, word.length)).toString().replace(","," ") 
    let name = label.toLowerCase().replace(" ","_")
    return(
      <TextField 
      required
      variant="outlined"
      label={label}
      name={name}
      type={type}
      onChange={handleChange(name)}
  ></TextField>
    )
  }

  return (
    <>
      {userField("Username")}
      {userField("Email")}
      {userField("Password", "password")}
      {userField("Password Confirmation", "password")}
    </>
  );
}
export default SignUpForm;
