import { Dialog, DialogContent, DialogTitle, Button } from "@material-ui/core";
import React, { useState } from "react";
import SignUpForm from "./SignUpForm";
import { toast, Flip } from "react-toastify";
import { Form } from "react-bootstrap";

function Signup({ setUser }) {
  const [firstName, setFirstName] = useState([]);
  const [lastName, setLastName] = useState([]);
  const [email, setEmail] = useState([]);
  const [username, setUsername] = useState([]);
  const [password, setPassword] = useState([]);
  const [passwordConfirmation, setPasswordConfirmation] = useState({name: "", value: ""});
  const [open, setOpen] = React.useState(false);

  const handleClickToOpen = () => {
    setOpen(true);
  };

  const handleToClose = () => {
    setOpen(false);
  };

  function handleSignUp(e) {
    e.preventDefault();
    const data = new FormData();
    data.append("username",username);
    data.append("first_name", firstName) 
    data.append("last_name",lastName)
    data.append("password",password)
    data.append("password_confirmation",passwordConfirmation)
    data.append("email",email)

    fetch("/signup", {
      method: "POST",
      body: JSON.stringify({
        username,
        first_name: firstName,
        last_name: lastName,
        email,
        password,
        password_confirmation: passwordConfirmation
      }),
    }).then((r) => {
      if (r.ok) {
        r.json().then((user) => setUser(user));
        handleToClose()
      } else {
        toast.error( r.errors, {
          autoClose: 1000,
          hideProgressBar: true,
          transition: Flip,
          position: "top-center"
        })
      }
    })
  }

  console.log(password)


  return (
    <>
      <Button variant="outlined" color="primary" onClick={handleClickToOpen}>
        Signup
      </Button>

      <Dialog open={open} onClose={handleToClose} onSubmit={handleSignUp}>
        <DialogTitle>{"Sign Up"}</DialogTitle>
        <DialogContent>
          <Form onSubmit={handleSignUp}>
            <SignUpForm 
            setUsername={setUsername}
            setEmail={setEmail}
            setPassword={setPassword}
            setPasswordConfirmation={setPasswordConfirmation}
            setFirstName={setFirstName}
            setLastName={setLastName}
            handleSignUp={handleSignUp}
            handleToClose={handleToClose}
            />
                   <Button type="submit" color="primary" autoFocus>
          Submit
        </Button>
        <Button onClick={handleToClose} color="secondary" autoFocus>
          Close
        </Button>
            </Form>
        </DialogContent>

      </Dialog>
    </>
  );
}
export default Signup;
