import { useEffect, useState } from "react";

function PeraHat(){

const URL = "https://joepera.com/product/spruce-hat/"
const [stock, setStock] = useState("")

useEffect(()=>{
  fetch(URL)
  .then(r => r.text())
  .then(s => {
    let parser = new DOMParser();
    let doc = parser.parseFromString(s, 'text/html')

    // Get the image file
    let stock = doc.querySelector('.stock')
    stock.className === "stock in-stock" ? setStock("In Stock") : setStock("Out of Stock")
  })
},[])


return (
  <a href={URL} ><button>{stock}</button></a>  
  )
}
export default PeraHat
